CREATE PROCEDURE [chemopx].[sp_ChemotherapyPX_Get]
	@Id int
AS
BEGIN
SELECT px.[Id], px.[CODE], pc.Proc_Desc as [CODE_DESC], px.[GENERIC_NAME], px.[TRADE_NAME], px.[CKPT_INHIB_IND], px.[ANTI_EMETIC_IND],  pc.Proc_Cd_Type as [CODE_TYPE], px.[CODE_EFF_DT], pc.Proc_Cd_Date as [CODE_END_DT], px.[NHNR_CANCER_THERAPY], cc.[Code_Category] as CODE_CATEGORY, ISNULL(px.[CODE_CATEGORY_ID],0) as CODE_CATEGORY_ID, asp.[ASP_CATEGORY] as ASP_CATEGORY,  ISNULL(px.[ASP_CATEGORY_ID],0) as ASP_CATEGORY_ID,  da.[DRUG_ADM_MODE] as DRUG_ADM_MODE,  ISNULL(px.[DRUG_ADM_MODE_ID],0) as DRUG_ADM_MODE_ID, pa.[PA_DRUGS] as PA_DRUGS, ISNULL(px.[PA_DRUGS_ID],0) as PA_DRUGS_ID , px.[PA_EFF_DT], px.[PA_END_DT], cp.[CEP_PAY_CD] as CEP_PAY_CD, ISNULL(px.[CEP_PAY_CD_ID],0) as CEP_PAY_CD_ID, ce.[CEP_ENROLL_CD] as CEP_ENROLL_CD, ISNULL(px.[CEP_ENROLL_CD_ID],0) as CEP_ENROLL_CD_ID, px.[CEP_ENROLL_EXCL_DESC], px.[NOVEL_STATUS_IND], px.[FIRST_NOVEL_MNTH], px.[SOURCE], px.[UPDATE_DT] FROM [chemopx].[ChemotherapyPX] px LEFT JOIN [chemopx].[Code_Category] cc ON cc.CODE_CATEGORY_ID = px.CODE_CATEGORY_ID LEFT JOIN [chemopx].[ASP_Category] asp ON asp.ASP_CATEGORY_ID = px.[ASP_CATEGORY_ID] LEFT JOIN [chemopx].[Drug_Adm_Mode] da ON da.DRUG_ADM_MODE_ID = px.[DRUG_ADM_MODE_ID] LEFT JOIN [chemopx].[PA_Drugs] pa ON pa.PA_DRUGS_ID = px.[PA_DRUGS_ID] LEFT JOIN [chemopx].[CEP_Pay_Cd] cp ON cp.CEP_PAY_CD_ID = px.[CEP_PAY_CD_ID] LEFT JOIN [chemopx].[CEP_Enroll_Cd] ce ON ce.CEP_ENROLL_CD_ID= px.[CEP_ENROLL_CD_ID] LEFT JOIN [vct].[Proc_Codes] pc ON pc.Proc_Cd  = px.CODE WHERE  px.[Id] = @Id;
END
